import { Component } from '@angular/core';

@Component({
  selector: `
  button[awesome-button],
  input[type=button][awesome-button],
  input[type=file][awesome-button]
  `,
  standalone: true,
  template: `<ng-content />`,
  host: {
    class: 'awesome-button',
  },
})
export class AwesomeButton {}

