import { BehaviorSubject } from 'rxjs'; 

export function createStore(initialState) {
  const subject = new BehaviorSubject(initialState);
  return {
    select: (fn) => subject.pipe(map(fn)),
    setState: (state) => subject.next(state),
  };
}

